    public static Map<String,String> emojiMap=new HashMap();

    static {
        EmojiUtils.utf16toEntities(emoji);
    }
    /**
     * 将emoji字符串转&#324234;类型实体字符
     * @param str
     * @return
     */
    public static String utf16toEntities(String str){
        try {
            str = str.replaceAll("%", "％");
//        str=unescape(str);
            str = StringEscapeUtils.unescapeJava(str);
            Pattern p = Pattern.compile("([\\x{10000}-\\x{10ffff}\ud800-\udfff])", Pattern.CASE_INSENSITIVE);
            Matcher m = p.matcher(str);
            while (m.find()) {
                String chars = m.group();
                int H, L, code;
                if (chars.length() == 2) {
                    H = chars.charAt(0);
                    L = chars.charAt(1);
                    code = (H - 0xD800) * 0x400 + 0x10000 + L - 0xDC00;
                    String codestr = "&#" + code + ";";
                    str = str.replace(chars, codestr);
                    emojiMap.put(codestr, chars);
                }
            }
        }catch (Exception e ){
            e.printStackTrace();
        }
        return str;
    }

    public static String codeToEmoji(String str){
        try {
            Pattern p = Pattern.compile("&#(\\d){6};", Pattern.CASE_INSENSITIVE);
            Matcher m = p.matcher(str);
            while (m.find()) {
                String chars = m.group();
                if(emojiMap.containsKey(chars)){
                    String emoji=emojiMap.get(chars);
                    str=str.replace(chars, emoji);
                }
            }
        }catch (Exception e){
        }
        return str;
    }

    public static String unescape(String src) {
        StringBuffer tmp = new StringBuffer();
        tmp.ensureCapacity(src.length());
        int lastPos = 0, pos = 0;
        char ch;
        while (lastPos < src.length()) {
            pos = src.indexOf("%", lastPos);
            if (pos == lastPos) {
                if (src.charAt(pos + 1) == 'u') {
                    ch = (char) Integer.parseInt(src
                            .substring(pos + 2, pos + 6), 16);
                    tmp.append(ch);
                    lastPos = pos + 6;
                } else {
                    ch = (char) Integer.parseInt(src
                            .substring(pos + 1, pos + 3), 16);
                    tmp.append(ch);
                    lastPos = pos + 3;
                }
            } else {
                if (pos == -1) {
                    tmp.append(src.substring(lastPos));
                    lastPos = src.length();
                } else {
                    tmp.append(src.substring(lastPos, pos));
                    lastPos = pos;
                }
            }
        }
        return tmp.toString();
    }

